import React, { useState, useEffect } from "react";
import '../styles.css';
import starwars from "../APIs/starwars";
import covid from "../APIs/covid";
import cats from "../APIs/cats";

function MainFunctional() {
  const [peopleData, setPeopleData] = useState([]);
  const [planetData, setPlanetData] = useState([]);
  const [starShipData, setStarshipData] = useState([]);
  const [filmData, setFilmData] = useState([]);
  const [speciesData, setSpeciesData] = useState([]);
  const [vehicleData, setVehicleData] = useState([]);


  useEffect(() => {
    starwars.getPeople().then((response) => {
      console.log("response", response);
      setPeopleData(response);
    });

    starwars.getPlanets().then((response) => {
        console.log("response", response);
        setPlanetData(response);
    });

    starwars.getStarships().then((response) => {
        console.log("response", response);
        setStarshipData(response);
    });

    starwars.getFilms().then((response) => {
        console.log("response", response);
        setFilmData(response);
    });

    starwars.getSpecies().then((response) => {
        console.log("response", response);
        setSpeciesData(response);
    });

    starwars.getVehicles().then((response) => {
        console.log("response", response);
        setVehicleData(response);
    });
  }, []);

    return (
        <div className="App">
        <h1>
            STARWARS
        </h1>
        <div>
        <Dropdown placeHolder="Select..."/>
            {peopleData.map(item => {
                return  <div key={item.index}>
                    name: {item.name}
                </div>
            })}
        </div>
        <div>
            {planetData.map(item => {
                return  <div key={item.index}>
                    name: {item.name}
                </div>
            })}
        </div>
        </div>
    );
}

class Header extends Component {
    render(){
      return (
        <div className="Header">

        </div>
      );
    }
  }

const Icon = () => {
    return (
        <svg height="20" width="20" viewBox="0 0 20 20">
          <path d="M4.516 7.548c0.436-0.446 1.043-0.481 1.576 0l3.908 3.747 3.908-3.747c0.533-0.481 1.141-0.446 1.574 0 0.436 0.445 0.408 1.197 0 1.615-0.406 0.418-4.695 4.502-4.695 4.502-0.217 0.223-0.502 0.335-0.787 0.335s-0.57-0.112-0.789-0.335c0 0-4.287-4.084-4.695-4.502s-0.436-1.17 0-1.615z"></path>
        </svg>
      );
};

const Dropdown = ({ placeHolder }) => {
    const getDisplay = () => {
      return placeHolder;
    };

    return (
      <div className="dropdown-container">
        <div className="dropdown-input">
          <div className="dropdown-selected-value">{getDisplay()}</div>
          <div className="dropdown-tools">
            <div className="dropdown-tool">
              <Icon />
            </div>
          </div>
        </div>
      </div>
    );
  };

export default MainFunctional;
