import React, { useState, useEffect, useMemo } from "react";
import Table from './Table'
import './Dropdown.css';
import starwars from "./APIs/starwars";
import yoda from "./components/yoda.gif";
import bb8 from "./components/bb8.gif";
import ship from "./components/ship.gif";

const Icon = () => {
    return (
        <svg height="20" width="20" viewBox="0 0 20 20">
          <path d="M4.516 7.548c0.436-0.446 1.043-0.481 1.576 0l3.908 3.747 3.908-3.747c0.533-0.481 1.141-0.446 1.574 0 0.436 0.445 0.408 1.197 0 1.615-0.406 0.418-4.695 4.502-4.695 4.502-0.217 0.223-0.502 0.335-0.787 0.335s-0.57-0.112-0.789-0.335c0 0-4.287-4.084-4.695-4.502s-0.436-1.17 0-1.615z"></path>
        </svg>
      );
};

const Dropdown = ({ placeHolder, options}) => {

    const [showMenu, setShowMenu] = useState(false);
    const [selectedValue, setSelectedValue] = useState(null);
    const [data, setData] = useState([]);
    const [columns, setColumns] = useState(null);

    useEffect(() => {
        const handler = () => setShowMenu(false);

        window.addEventListener("click", handler);
        return() => {
            window.removeEventListener("click", handler);
        };
    });

    const handleInputClick = (e) => {
        e.stopPropagation();
        setShowMenu(!showMenu);
    }

    const getDisplay = () => {
        if(selectedValue){
            return selectedValue.type;
        }
        return placeHolder;
    };

    const onItemClick = (option) => {
        setSelectedValue(option);
    }

    const isSelected = (option) => {
        if(!selectedValue){
            return false;
        }

        return selectedValue.value === option.value;
    }

    useEffect(() => {
        if(selectedValue?.type == "People"){
            starwars.getPeople().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Name",
                                accessor: "name",
                            },
                            {
                                Header: "Birth Date",
                                accessor: "birth_year",
                            },
                            {
                                Header: "Gender",
                                accessor: "gender",
                            },
                            {
                                Header: "Hair Color",
                                accessor: "hair_color",
                            },
                            {
                                Header: "Height",
                                accessor: "height",
                            },
                            {
                                Header: "Weight (Kilo)",
                                accessor: "mass",
                            },
                        ], []
                    )
            });
        }else if(selectedValue?.type == "Planets"){
            starwars.getPlanets().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Name",
                                accessor: "name",
                            },
                            {
                                Header: "Climate",
                                accessor: "climate",
                            },
                            {
                                Header: "Diameter (Kilometers)",
                                accessor: "diameter",
                            },
                            {
                                Header: "Gravity",
                                accessor: "gravity",
                            },
                            {
                                Header: "Orbital Period (days)",
                                accessor: "orbital_period",
                            },
                            {
                                Header: "Population",
                                accessor: "population",
                            },
                            {
                                Header: "Rotation Period (hrs)",
                                accessor: "rotation_period",
                            },
                            {
                                Header: "Surface Water (%)",
                                accessor: "surface_water",
                            },
                            {
                                Header: "Terrain",
                                accessor: "terrain",
                            },
                        ], []
                    )
            });
        }else if(selectedValue?.type == "Starships"){
            starwars.getStarships().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Name",
                                accessor: "name",
                            },
                            {
                                Header: "Model",
                                accessor: "model",
                            },
                            {
                                Header: "Manufacturer",
                                accessor: "manufacturer",
                            },
                            {
                                Header: "Starship Class",
                                accessor: "starship_class",
                            },
                            {
                                Header: "Speed (# of Megalights/Hr)",
                                accessor: "MGLT",
                            },
                            {
                                Header: "Length (meters)",
                                accessor: "length",
                            },
                            {
                                Header: "Passenger Capacity (non-essential)",
                                accessor: "passengers",
                            },
                            {
                                Header: "Max Atmosphering Speed (n/a if not possible)",
                                accessor: "max_atmosphering_speed",
                            },
                            {
                                Header: "Cargo Capacity (Kilo)",
                                accessor: "cargo_capacity",
                            },
                            {
                                Header: "Cost (galactic credits)",
                                accessor: "cost_in_credits",
                            },
                            {
                                Header: "Crew",
                                accessor: "crew",
                            },
                            {
                                Header: "Hyperdrive Rating",
                                accessor: "hyperdrive_rating",
                            },
                        ], []
                    )
            });
        }else if(selectedValue?.type == "Films"){
            starwars.getFilms().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Title",
                                accessor: "title",
                            },
                            {
                                Header: "Director",
                                accessor: "director",
                            },
                            {
                                Header: "Producer",
                                accessor: "producer",
                            },
                            {
                                Header: "Release Date",
                                accessor: "release_date",
                            },
                        ], []
                    )
            });
        }else if(selectedValue?.type == "Species"){
            starwars.getSpecies().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Name",
                                accessor: "name",
                            },
                            {
                                Header: "Skin Color",
                                accessor: "skin_colors",
                            },
                            {
                                Header: "Eye Colors",
                                accessor: "eye_colors",
                            },
                            {
                                Header: "Hair Colors",
                                accessor: "hair_colors",
                            },
                            {
                                Header: "Language",
                                accessor: "language",
                            },
                            {
                                Header: "Designation",
                                accessor: "designation",
                            },
                            {
                                Header: "Classification",
                                accessor: "classification",
                            },
                            {
                                Header: "Average Height (centimeters)",
                                accessor: "average_height",
                            },
                            {
                                Header: "Average Lifespan (years)",
                                accessor: "average_lifespan",
                            },
                        ], []
                    )
            });

        }else if(selectedValue?.type == "Vehicles"){
            starwars.getVehicles().then((response) => {
                setData(response);
                setColumns(
                    [
                            {
                                Header: "Name",
                                accessor: "name",
                            },
                            {
                                Header: "Model",
                                accessor: "model",
                            },
                            {
                                Header: "Manufacturer",
                                accessor: "manufacturer",
                            },
                            {
                                Header: "Vehicle Class",
                                accessor: "vehicle_class",
                            },
                            {
                                Header: "Cargo Capacity (kilos)",
                                accessor: "cargo_capacity",
                            },
                            {
                                Header: "Consumable Capacity",
                                accessor: "consumables",
                            },
                            {
                                Header: "Crew Size",
                                accessor: "crew",
                            },
                            {
                                Header: "Passenger Capacity",
                                accessor: "passengers",
                            },
                            {
                                Header: "Length (meters)",
                                accessor: "length",
                            },
                        ], []
                    )
            });
        }
    }, [selectedValue?.type]);

    return (
      <div className="dropdown-container">
        <img className="bb8" src ={bb8} alt="bb8" />
        <img className="yoda" src ={yoda} alt="yoda" />
        <h1 className="selectCategory">Select a Category: </h1>
        <div onClick={handleInputClick} className="dropdown-input">
            <div className="dropdown-selected-value">{getDisplay()}</div>
            <div className="dropdown-tools">
                <div className="dropdown-tool">
                    <Icon />
                </div>
            </div>
            {showMenu && (
                <div className="dropdown-menu">
                    {options.map((option) => (
                        <div 
                        onClick = {() => onItemClick(option)}
                        key={option.type} 
                        className={`dropdown-item ${isSelected(option) && "selected"}`}>
                            {option.type}
                        </div>
                    ))}
                </div>
            )}
        </div>
        <div>
            <br></br>
            {selectedValue && columns ? (
                <>
                <Table
                    columns={columns}
                    data={data}>
                </Table>
                </>
            ) : null}
        </div>
      </div>
    );
  };

  export default Dropdown;
